// Auto-select best rate based on multiple criteria
const rates = $input.first().json.data;

if (!rates || rates.length === 0) {
  return {
    selected_rate: null,
    selection_reason: 'No rates available'
  };
}

// Scoring algorithm for rate selection
const scoredRates = rates.map(rate => {
  let score = 0;

  // Prefer preferred rates (highest priority)
  if (rate.is_preferred) {
    score += 100;
  }

  // Prefer lower transit times (faster delivery)
  const transitDays = rate.transit_days || 999;
  score += Math.max(0, 50 - transitDays); // Max 50 points for fastest

  // Prefer competitive pricing (lower is better, but not too low)
  const price = rate.pricing.all_in_freight_sell || 0;
  const avgPrice = rates.reduce((sum, r) => sum + (r.pricing.all_in_freight_sell || 0), 0) / rates.length;
  if (price > 0 && price <= avgPrice * 1.1) { // Within 10% of average
    score += 30;
  }

  // Prefer longer validity periods
  const validFrom = new Date(rate.validity.from);
  const validTo = new Date(rate.validity.to);
  const validityDays = (validTo - validFrom) / (1000 * 60 * 60 * 24);
  score += Math.min(20, validityDays / 10); // Max 20 points

  // Prefer major carriers (reliability bonus)
  const majorCarriers = ['MSC', 'MAERSK', 'CMA CGM', 'COSCO', 'HAPAG-LLOYD', 'ONE'];
  if (majorCarriers.some(carrier => rate.vendor.toUpperCase().includes(carrier))) {
    score += 15;
  }

  return {
    ...rate,
    selection_score: score
  };
});

// Sort by score (highest first)
scoredRates.sort((a, b) => b.selection_score - a.selection_score);

const selectedRate = scoredRates[0];
const selectionReason = `Selected based on: ${selectedRate.is_preferred ? 'Preferred rate, ' : ''}Score: ${selectedRate.selection_score}, Transit: ${selectedRate.transit_days} days, Price: $${selectedRate.pricing.all_in_freight_sell}`;

return {
  selected_rate: selectedRate,
  selection_reason: selectionReason,
  all_rates: scoredRates,
  total_rates_found: rates.length
};

// Enhance quote with additional business logic and formatting
const originalQuote = $input.first().json.data;
const selectionData = $('Auto-Select Best Rate').first().json;
const webhookData = $('Auto Quote Webhook').first().json;

// Add auto-selection metadata
const enhancedQuote = {
  ...originalQuote,
  auto_selection: {
    selected_rate_id: selectionData.selected_rate.rate_id,
    selection_reason: selectionData.selection_reason,
    total_rates_considered: selectionData.total_rates_found,
    selection_algorithm: 'multi-criteria scoring (preferred, transit, price, validity, carrier)'
  },
  business_insights: {
    cost_breakdown: {
      ocean_freight_percentage: ((originalQuote.totals.ocean_freight_total / originalQuote.totals.grand_total_usd) * 100).toFixed(1),
      local_charges_percentage: (((originalQuote.totals.origin_total_usd + originalQuote.totals.destination_total_usd) / originalQuote.totals.grand_total_usd) * 100).toFixed(1)
    },
    competitiveness: {
      transit_time_days: originalQuote.quote_parts.ocean_freight.transit_days,
      price_per_container: originalQuote.totals.grand_total_usd / originalQuote.route.container_count,
      carrier_reliability: originalQuote.quote_parts.ocean_freight.carrier
    },
    recommendations: []
  }
};

// Add business recommendations
if (originalQuote.totals.grand_total_usd > 10000) {
  enhancedQuote.business_insights.recommendations.push('High-value shipment - consider insurance options');
}

if (originalQuote.quote_parts.ocean_freight.transit_days > 30) {
  enhancedQuote.business_insights.recommendations.push('Long transit time - consider expedited options if urgent');
}

if (originalQuote.totals.origin_total_usd + originalQuote.totals.destination_total_usd > originalQuote.totals.ocean_freight_total * 0.3) {
  enhancedQuote.business_insights.recommendations.push('High local charges - review port-specific costs');
}

// Add quote metadata
enhancedQuote.quote_metadata = {
  generated_by: 'auto_quote_system',
  generation_timestamp: new Date().toISOString(),
  request_source: 'n8n_workflow',
  original_request: {
    pol_code: webhookData.pol_code,
    pod_code: webhookData.pod_code,
    container_type: webhookData.container_type,
    container_count: webhookData.container_count || 1
  }
};

return enhancedQuote;

