// Parse email content to extract quote request details
const email = $input.first().json;
const subject = email.subject || '';
const textContent = email.text || '';
const htmlContent = email.html || '';

// Combine text and HTML content for parsing
const fullContent = textContent + ' ' + htmlContent.replace(/<[^>]*>/g, ' ');

// Initialize parsed data
const parsedData = {
  original_email: {
    from: email.from,
    to: email.to,
    subject: email.subject,
    date: email.date,
    message_id: email.messageId
  },
  customer_email: email.from,
  customer_name: email.from.split('@')[0].replace(/[._]/g, ' '),
  // Default values
  pol_code: null,
  pod_code: null,
  container_type: null,
  container_count: 1,
  salesforce_opportunity_id: null,
  salesforce_org_id: '00000000-0000-0000-0000-000000000001'
};

// Extract POL (Port of Loading) - common patterns
const polPatterns = [
  /(?:from|origin|pol|loading)\s*:?\s*([A-Z]{5})/gi,
  /(?:from|origin|pol|loading)\s*:?\s*([A-Z]{4}[A-Z0-9])/gi,
  /(?:from|origin|pol|loading)\s*:?\s*([A-Z]{3}[A-Z0-9]{2})/gi
];

// Extract POD (Port of Discharge) - common patterns
const podPatterns = [
  /(?:to|destination|pod|discharge)\s*:?\s*([A-Z]{5})/gi,
  /(?:to|destination|pod|discharge)\s*:?\s*([A-Z]{4}[A-Z0-9])/gi,
  /(?:to|destination|pod|discharge)\s*:?\s*([A-Z]{3}[A-Z0-9]{2})/gi
];

// Extract container type
const containerPatterns = [
  /(\d+)\s*(?:x\s*)?(20GP|40GP|40HC|45HC|20FT|40FT)/gi,
  /(20GP|40GP|40HC|45HC|20FT|40FT)/gi
];

// Extract container count
const countPatterns = [
  /(\d+)\s*(?:x\s*)?(?:container|box|unit)/gi,
  /(\d+)\s*(?:x\s*)?(20GP|40GP|40HC|45HC|20FT|40FT)/gi
];

// Extract Salesforce Opportunity ID
const salesforcePatterns = [
  /(?:opportunity|opp|sf)\s*:?\s*([0-9a-fA-F]{15}|[0-9a-fA-F]{18})/gi,
  /(?:opportunity|opp|sf)\s*:?\s*(006[a-zA-Z0-9]{12,15})/gi
];

// Parse POL
for (const pattern of polPatterns) {
  const match = pattern.exec(fullContent);
  if (match && match[1]) {
    parsedData.pol_code = match[1].toUpperCase();
    break;
  }
}

// Parse POD
for (const pattern of podPatterns) {
  const match = pattern.exec(fullContent);
  if (match && match[1]) {
    parsedData.pod_code = match[1].toUpperCase();
    break;
  }
}

// Parse container type and count
for (const pattern of containerPatterns) {
  const match = pattern.exec(fullContent);
  if (match) {
    if (match[1] && !isNaN(match[1])) {
      parsedData.container_count = parseInt(match[1]);
      parsedData.container_type = match[2] || match[1];
    } else {
      parsedData.container_type = match[1] || match[0];
    }
    break;
  }
}

// Parse container count separately if not found above
if (parsedData.container_count === 1) {
  for (const pattern of countPatterns) {
    const match = pattern.exec(fullContent);
    if (match && match[1] && !isNaN(match[1])) {
      parsedData.container_count = parseInt(match[1]);
      break;
    }
  }
}

// Parse Salesforce Opportunity ID
for (const pattern of salesforcePatterns) {
  const match = pattern.exec(fullContent);
  if (match && match[1]) {
    parsedData.salesforce_opportunity_id = match[1];
    break;
  }
}

// Validate required fields
const validation = {
  has_pol: !!parsedData.pol_code,
  has_pod: !!parsedData.pod_code,
  has_container_type: !!parsedData.container_type,
  has_valid_route: !!(parsedData.pol_code && parsedData.pod_code && parsedData.container_type)
};

// Add validation results
parsedData.validation = validation;
parsedData.is_valid_request = validation.has_valid_route;

// Add parsing confidence score
let confidence = 0;
if (validation.has_pol) confidence += 25;
if (validation.has_pod) confidence += 25;
if (validation.has_container_type) confidence += 25;
if (parsedData.container_count > 1) confidence += 10;
if (parsedData.salesforce_opportunity_id) confidence += 15;

parsedData.parsing_confidence = confidence;

// Add debug info
parsedData.debug = {
  original_content: fullContent.substring(0, 500) + '...',
  extracted_patterns: {
    pol_found: parsedData.pol_code,
    pod_found: parsedData.pod_code,
    container_found: parsedData.container_type,
    count_found: parsedData.container_count,
    salesforce_found: parsedData.salesforce_opportunity_id
  }
};

return parsedData;

// Format quote data for email response and Salesforce update
const quoteData = $input.first().json.data;
const originalRequest = $('Parse Email Content').first().json;

// Extract key information
const route = quoteData.route;
const totals = quoteData.totals;
const oceanFreight = quoteData.quote_parts.ocean_freight;
const originCharges = quoteData.quote_parts.origin_charges;
const destCharges = quoteData.quote_parts.destination_charges;

// Format for email
const emailData = {
  to: originalRequest.customer_email,
  subject: `Re: ${originalRequest.original_email.subject} - Freight Quote Ready`,
  quote_number: `QT-${Date.now()}`,
  customer_name: originalRequest.customer_name,
  route_display: quoteData.quote_summary.route_display,
  container_info: quoteData.quote_summary.container_info,
  carrier: oceanFreight.carrier,
  transit_days: oceanFreight.transit_days,
  validity: oceanFreight.validity,
  pricing: {
    ocean_freight: totals.ocean_freight_total,
    origin_charges: totals.origin_total_usd,
    destination_charges: totals.destination_total_usd,
    grand_total: totals.grand_total_usd,
    currency: totals.currency
  },
  breakdown: {
    ocean_freight_per_container: oceanFreight.all_in_freight_sell,
    origin_charges_per_container: originCharges.total_usd,
    destination_charges_per_container: destCharges.total_usd,
    total_per_container: totals.grand_total_usd / route.container_count
  },
  charges_detail: {
    origin: originCharges.charges.map(c => ({
      name: c.charge_name,
      amount: c.amount_usd,
      currency: c.charge_currency
    })),
    destination: destCharges.charges.map(c => ({
      name: c.charge_name,
      amount: c.amount_usd,
      currency: c.charge_currency
    }))
  },
  generated_at: new Date().toISOString(),
  valid_until: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
  original_message_id: originalRequest.original_email.message_id,
  original_subject: originalRequest.original_email.subject
};

// Format for Salesforce update
const salesforceData = {
  opportunity_id: originalRequest.salesforce_opportunity_id,
  quote_status: 'Generated',
  quote_number: emailData.quote_number,
  total_amount: totals.grand_total_usd,
  currency: totals.currency,
  carrier: oceanFreight.carrier,
  transit_days: oceanFreight.transit_days,
  quote_valid_until: emailData.valid_until,
  quote_generated_at: emailData.generated_at,
  route_pol: route.pol,
  route_pod: route.pod,
  container_type: route.container_type,
  container_count: route.container_count,
  pricing_breakdown: {
    ocean_freight: totals.ocean_freight_total,
    origin_charges: totals.origin_total_usd,
    destination_charges: totals.destination_total_usd
  },
  email_triggered: true,
  original_email_from: originalRequest.customer_email
};

return {
  email_data: emailData,
  salesforce_data: salesforceData,
  original_quote: quoteData,
  original_request: originalRequest
};
